(function(){
  const params = new URLSearchParams(window.location.search);
  const link = document.createElement('a');
  link.href = "https://ratpi-studio.fr";
  link.target = "_blank";
  link.rel = "noopener noreferrer";
  link.className = "ratpi-badge";
  link.style.cssText = `
    margin: auto;
    backdrop-filter: blur(8px);
    -webkit-backdrop-filter: blur(8px);
    border-radius: 12px;
    padding: 0.4rem 0.8rem;
    display: inline-flex;
    align-items: center;
    gap: 0.4rem;
    font-size: ${params.get('fontsize') || '0.75rem'};
    color: white;
    text-decoration: none;
    font-family: sans-serif;
    border: 1px solid rgba(255, 255, 255, 0.3);
    background: rgba(255, 255, 255, 0.15);
    overflow: hidden;
  `;
  if (params.get('theme') === 'light') {
    link.style.background = 'rgba(0, 0, 0, 0.15)';
    link.style.color = 'white';
    link.style.border = '1px solid rgba(0, 0, 0, 0.3)';
  }
  link.innerHTML = `<span>Made with</span> <span class="mouse">üê≠</span> <span>by Ratpi Studio!</span>`;
  document.currentScript.parentNode.insertBefore(link, document.currentScript);
  if (params.get('animate') === 'loop') {
    link.querySelector('.mouse').style.animation = 'wiggle 1.5s infinite ease-in-out';
  }
})();
